"use strict";let bitcoinCode,stellarCode;const divs=document.querySelectorAll('[data-bistow-widget-type="1"]');for(const t of divs){bitcoinCode=t.dataset.bitcoin,stellarCode=t.dataset.stellar;const e=generateTemplate();t.innerHTML=e}function generateTemplate(){const t=`${(new Date).getTime()}${Math.floor(Math.random()*Math.floor(1e4))}`;return`\n        <style>\n            .bistow-type-1 {\n                display: inline-block;\n                max-width: 300px;\n            }\n            .bistow-type-1-bitcoin${t}, .bistow-type-1-stellar${t} {\n                display: none;\n            }\n            .bistow-type-1-radio${t} {\n                display: none;\n            }\n            .bistow-type-1-radio${t} + label{\n                background-color: #fff;\n                box-shadow: inset 0 0 0 2px #202e37;\n                box-sizing: border-box;\n                cursor: pointer;\n                color: #202e37;\n                display: inline-block;\n                height: 30px;\n                line-height: 30px;\n                font-size: 20px;\n                font-family: sans-serif;\n                text-align: center;\n                text-shadow: 0 2px 4px rgba(0,0,0,.25);\n                width: 48%;\n            }\n            .bistow-type-1-radio${t} + label:hover{\n                box-shadow: inset 0 0 0 2px #75b9be;\n                color: #75B9BE;\n                transition: all 0.4s;\n            }\n            .bistow-type-1-radio${t}:checked + label{\n                background-color: #202e37;\n                color: #fff;\n            }\n            .bistow-type-1-radio-bitcoin${t}:checked ~ .bistow-type-1-bitcoin${t} {\n                display:block;\n            }\n            .bitstow-type-1-radio-stellar${t}:checked ~ .bistow-type-1-stellar${t} {\n                display:block;\n            }\n        </style>\n        <input class="bistow-type-1-radio-bitcoin${t} bistow-type-1-radio${t}" \n            name="bistow-type-1-group${t}" \n            id="bistow-type-1-radio-bitcoin${t}" type="radio" checked="checked" />\n        <label class="label" for="bistow-type-1-radio-bitcoin${t}">Bitcoin</label>\n        <input class="bitstow-type-1-radio-stellar${t} bistow-type-1-radio${t}" \n            name="bistow-type-1-group${t}" \n            id="bitstow-type-1-radio-stellar${t}" type="radio" />\n        <label class="label" for="bitstow-type-1-radio-stellar${t}">Stellar</label>\n        ${getCodes(bitcoinCode,`bistow-type-1-bitcoin${t}`)}\n        ${getCodes(stellarCode,`bistow-type-1-stellar${t}`)}\n    `}function getCodes(t,e){return t?`<img src="${t}" class="${e}"/>`:""}
